mapper.sql:
where 1=1


        and a.id = #{id}


        and a.did = #{did}


        and a.order_no = #{orderNo}


        and a.order_money = #{orderMoney}


        and a.collection_account_id = #{collectionAccountId}


        and a.collection_type = #{collectionType}


        and a.wx_nickname = #{wxNickname}


        and a.wx_id = #{wxId}


        and a.order_status = #{orderStatus}


        and a.order_status >= 3


        and a.is_reply = #{isReply}


        and a.replenish_type = #{replenishType}


        and a.curday = #{curday}


        and a.curday >= #{curdayStart} and a.curday <= #{curdayEnd}


        and a.run_status = 3

    and a.yn = 0

mapper.select:
select

from tb_fn_order a left join tb_fn_did_collection_account b on a.collection_account_id = b.id



    limit #{start},#{end}

select a.id,a.did,a.order_no,a.order_money,a.collection_account_id,a.collection_type, a.qr_code,a.wx_nickname,a.wx_id,a.out_trade_no,a.notify_url,a.order_status,a.invalid_time, a.user_id,a.zfb_ac_num, a.curday,a.curhour,a.curminute,a.run_num,a.run_status, a.create_time,a.update_time,a.yn, time_to_sec(timediff(invalid_time,now())) as invalid_second from tb_fn_order a where 1=1 and a.order_no = #{orderNo} and a.order_status = 1
and a.did_status = #{didStatus}
and a.yn = 0 limit 1

select ifnull(sum(convert(a.order_money,decimal(19,2))), '0.00') as money from tb_fn_order a
where 1=1
and a.order_status = #{orderStatus}
and a.curday = #{curday}

    and a.did in

        #{item}


and a.collection_type = 2
and a.yn = 0
